$(document).on("click",".commentDeleteBtn",function(){if(confirm("정말 삭제 하시겠습니까?")===!1)return!1;var e=$(this);return $.ajax({method:"POST",url:"/api/comments/delete",data:{type:e.data("type"),type_comment_id:e.data("id")}}).done(function(e){"delete"===e.body.result_type?($("#comment_"+e.body.type_comment_id).remove(),$(".commentArea .number").text(e.body.comment_count)):alert(e.body.message)}).fail(function(){alert("서버에 문제가 있어 정상적으로 처리되지 않았습니다. \r\n잠시 후에 다시 시도해주세요.")}),!1});